import { useState, useEffect } from "react";

// `https://api.frankfurter.app/latest?amount=100&from=EUR&to=USD`

export default function App() {
  const [amount, setAmount] = useState(100);
  const [amountOutput, setAmountOutput] = useState(0);
  const [curFrom, setCurFrom] = useState("EUR");
  const [curTo, setCurTo] = useState("USD");

  useEffect(function () {
    async function fetchCurrency() {
      const response = await fetch(
        `https://api.frankfurter.app/latest?amount=${amount}&from=${curFrom}&to=${curTo}`
      );
      const data = await response.json();
      console.log(data);
    }
  });

  return (
    <div>
      <input
        type="text"
        value={amount}
        onChange={(e) => setAmount(e.target.value)}
      />
      <select value={curFrom} onChange={(e) => e.target.value}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <select value={curTo} onChange={(e) => e.target.value}>
        <option value="USD">USD</option>
        <option value="EUR">EUR</option>
        <option value="CAD">CAD</option>
        <option value="INR">INR</option>
      </select>
      <p>{amount}</p>
    </div>
  );
}
